  #if ENABLED (JERRY_ES2015_BUILTIN_TYPEDARRAY)
            if (ecma_object_is_typedarray (obj_p))
            {
              ecma_op_typedarray_list_lazy_property_names (obj_p, prop_names_p);
            }
  #endif /* ENABLED (JERRY_ES2015_BUILTIN_TYPEDARRAY) */
