    {
      ecma_string_t *name_p = ecma_get_string_from_value (buffer_p[i]);

      if (ecma_string_get_array_index (name_p) != ECMA_STRING_NOT_ARRAY_INDEX)
      {
        array_index_named_properties_count++;
      }
#if ENABLED (JERRY_ES2015)
