        if(tvb_get_guint8(tvb, offset+0)&0xc0){
            return 0;
        }
        /* byte 1 must be 0x80 */
        if(tvb_get_guint8(tvb, offset+1)!=0x80){
            return 0;
        }
        /* bytes 2 and 3 must be 0 */
        if(tvb_get_guint8(tvb, offset+2)||tvb_get_guint8(tvb, offset+3)){
            return 0;
        }
        break;
    case ISCSI_OPCODE_NOP_OUT:
