	{
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      maxchain -= gnusymidx;

      if (fseek (filedata->handle,
		 (archive_file_offset
		  + offset_from_vma (filedata, buckets_vma
