	  }

  no_gnu_hash:
      if (gnu_hash_error)
	{
	  if (mipsxlat)
	    {
	      free (mipsxlat);
	      mipsxlat = NULL;
	    }
	  if (gnuchains)
	    {
	      free (gnuchains);
