  const bool is_symbols = (opts & ECMA_LIST_SYMBOLS) != 0;
  const bool is_symbols_only = (opts & ECMA_LIST_SYMBOLS_ONLY) != 0;
#endif /* ENABLED (JERRY_ES2015) */

