        case 0x80:
        case 0x40:
        case 0x00:
            break;
        default:
            return 0;
        }
        /* CSG and NSG must not be 2 */
        if(((tmpbyte & 0x03) == 0x02)
           || ((tmpbyte & 0x0c) == 0x08)) {
            return 0;
        }
