    if ( !(op->flags & GNTMAP_readonly) &&
         !(act->pin & (GNTPIN_hstw_mask|GNTPIN_devw_mask)) )
        clear_flags |= GTF_writing;

    if ( !act->pin )
        clear_flags |= GTF_reading;

    if ( clear_flags )
        gnttab_clear_flags(rd, clear_flags, status);

