             !(kind & MAPKIND_WRITE) )
            kind = IOMMUF_readable | IOMMUF_writable;
        else if ( !kind )
            kind = IOMMUF_readable;
        else
            kind = 0;
        if ( kind && iommu_legacy_map(ld, _dfn(mfn_x(mfn)), mfn, 0, kind) )
