        clear_flags |= GTF_reading;

    if ( clear_flags )
        gnttab_clear_flags(rd, clear_flags, status);

 act_release_out:
    active_entry_release(act);

 unlock_out:
    grant_read_unlock(rgt);
    op->status = rc;
    put_maptrack_handle(lgt, handle);
    rcu_unlock_domain(rd);
