    print_help (argbuffer);

  if (nincluded_files == 0 && file_count == 0)
    {
      error ("no input files specified.");
      suggest_asking_for_help ();
    }

  if (tagfile == NULL)
    tagfile = savestr (CTAGS ? "tags" : "TAGS");
  cwd = etags_getcwd ();	/* the current working directory */
  if (cwd[strlen (cwd) - 1] != '/')
    {
      char *oldcwd = cwd;
      cwd = concat (oldcwd, "/", "");
      free (oldcwd);
    }

  /* Compute base directory for relative file names. */
  if (streq (tagfile, "-")
      || strneq (tagfile, "/dev/", 5))
    tagfiledir = cwd;		 /* relative file names are relative to cwd */
  else
    {
      canonicalize_filename (tagfile);
      tagfiledir = absolute_dirname (tagfile, cwd);
    }

  linebuffer_init (&lb);
  linebuffer_init (&filename_lb);
  linebuffer_init (&filebuf);
  linebuffer_init (&token_name);

  if (!CTAGS)
    {
      if (streq (tagfile, "-"))
	{
	  tagf = stdout;
	  set_binary_mode (STDOUT_FILENO, O_BINARY);
	}
      else
	tagf = fopen (tagfile, append_to_tagfile ? "ab" : "wb");
      if (tagf == NULL)
	pfatal (tagfile);
    }

  /*
   * Loop through files finding functions.
   */
  for (i = 0; i < current_arg; i++)
    {
      static language *lang;	/* non-NULL if language is forced */
      char *this_file;

      switch (argbuffer[i].arg_type)
	{
	case at_language:
	  lang = argbuffer[i].lang;
	  break;
	case at_regexp:
	  analyze_regex (argbuffer[i].what);
	  break;
	case at_filename:
	      this_file = argbuffer[i].what;
	      /* Input file named "-" means read file names from stdin
		 (one per line) and use them. */
	      if (streq (this_file, "-"))
		{
		  if (parsing_stdin)
		    fatal ("cannot parse standard input "
			   "AND read file names from it");
		  while (readline_internal (&filename_lb, stdin, "-") > 0)
		    process_file_name (filename_lb.buffer, lang);
		}
	      else
		process_file_name (this_file, lang);
	  break;
        case at_stdin:
          this_file = argbuffer[i].what;
          process_file (stdin, this_file, lang);
          break;
	default:
	  error ("internal error: arg_type");
	}
    }

  free_regexps ();
  free (lb.buffer);
  free (filebuf.buffer);
  free (token_name.buffer);

  if (!CTAGS || cxref_style)
    {
      /* Write the remaining tags to tagf (ETAGS) or stdout (CXREF). */
      put_entries (nodehead);
      free_tree (nodehead);
      nodehead = NULL;
      if (!CTAGS)
	{
	  fdesc *fdp;

	  /* Output file entries that have no tags. */
	  for (fdp = fdhead; fdp != NULL; fdp = fdp->next)
	    if (!fdp->written)
	      fprintf (tagf, "\f\n%s,0\n", fdp->taggedfname);

	  while (nincluded_files-- > 0)
	    fprintf (tagf, "\f\n%s,include\n", *included_files++);

	  if (fclose (tagf) == EOF)
	    pfatal (tagfile);
	}

      return EXIT_SUCCESS;
    }

  /* From here on, we are in (CTAGS && !cxref_style) */
  if (update)
    {
      char *cmd =
	xmalloc (strlen (tagfile) + whatlen_max +
		 sizeof "mv..OTAGS;grep -Fv '\t\t' OTAGS >;rm OTAGS");
      for (i = 0; i < current_arg; ++i)
	{
	  switch (argbuffer[i].arg_type)
	    {
	    case at_filename:
	    case at_stdin:
	      break;
	    default:
	      continue;		/* the for loop */
	    }
	  char *z = stpcpy (cmd, "mv ");
	  z = stpcpy (z, tagfile);
	  z = stpcpy (z, " OTAGS;grep -Fv '\t");
	  z = stpcpy (z, argbuffer[i].what);
	  z = stpcpy (z, "\t' OTAGS >");
	  z = stpcpy (z, tagfile);
	  strcpy (z, ";rm OTAGS");
	  if (system (cmd) != EXIT_SUCCESS)
	    fatal ("failed to execute shell command");
	}
      free (cmd);
      append_to_tagfile = true;
    }

  tagf = fopen (tagfile, append_to_tagfile ? "ab" : "wb");
  if (tagf == NULL)
    pfatal (tagfile);
  put_entries (nodehead);	/* write all the tags (CTAGS) */
  free_tree (nodehead);
  nodehead = NULL;
  if (fclose (tagf) == EOF)
    pfatal (tagfile);

  if (CTAGS)
    if (append_to_tagfile || update)
      {
	/* Maybe these should be used:
	   setenv ("LC_COLLATE", "C", 1);
	   setenv ("LC_ALL", "C", 1); */
	char *cmd = xmalloc (8 * strlen (tagfile) + sizeof "sort -u -o '' ''");
#if defined WINDOWSNT || defined MSDOS
	/* Quote "like this".  No need to escape the quotes in the file name,
	   since it is not allowed in file names on these systems.  */
	char *z = stpcpy (cmd, "sort -u -o \"");
	z = stpcpy (z, tagfile);
	z = stpcpy (z, "\" \"");
	z = stpcpy (z, tagfile);
	stpcpy (z, "\"");
#else
	/* Quote 'like this', and escape the apostrophe in the file name.  */
	char *z = stpcpy (cmd, "sort -u -o '");
	char *escaped_tagfile = z;
	for (; *tagfile; *z++ = *tagfile++)
	  if (*tagfile == '\'')
	    z = stpcpy (z, "'\\'");
	ptrdiff_t escaped_tagfile_len = z - escaped_tagfile;
	z = stpcpy (z, "' '");
	z = mempcpy (z, escaped_tagfile, escaped_tagfile_len);
	strcpy (z, "'");
#endif
	return system (cmd);
      }
  return EXIT_SUCCESS;
}

