		J9Class *methodClass = J9_CLASS_FROM_METHOD(method);
		UDATA initStatus = methodClass->initializeStatus;
		if (jitCompileTimeResolve && (J9ClassInitSucceeded != initStatus)) {
			method = NULL;
			goto done;
		}
