	json_object_object_add(jobj, "audio_levels", jarr);

	if (pmb) {
		pmb->pos = 0;
		mbuf_strdup(pmb, anon_str, pmb->end);
		mem_deref(pmb);
	}

	jzon_encode(json_str, jobj);
 out:	
	mem_deref(jobj);

	return err;
