	if (canRunJavaCode) {
		if (!throwException) {
			lookupOptions = J9_LOOK_NO_THROW;			
		}
	} else {
		lookupOptions = J9_LOOK_NO_JAVA;
	}

	Trc_VM_resolveStaticMethodRef_Entry(vmStruct, ramCP, cpIndex, resolveFlags);

tryAgain:
	romMethodRef = (J9ROMMethodRef *)&ramCP->romConstantPool[cpIndex];
