	/* Stack allocate a J9NameAndSignature structure for polymorphic signature methods */
	J9NameAndSignature onStackNAS = {NULL, NULL};
	J9UTF8 nullSignature = {0};
#endif /* defined(J9VM_OPT_OPENJDK_METHODHANDLE) */

