					&& (J9CPTYPE_INTERFACE_METHOD != cpType)
					) {
						goto incompat;
					}
				} else {
					if ((J9CPTYPE_INTERFACE_STATIC_METHOD == cpType)
					|| (J9CPTYPE_INTERFACE_INSTANCE_METHOD == cpType)
					|| (J9CPTYPE_INTERFACE_METHOD == cpType)
					) {
						goto incompat;
					}
				}
			}
