	left_ot = (op_table_t *) so->so_tables->data;
	sqlo_add_table_ref (so, &tree->_.join.right, is_jtc ? &res_jt : res);
	right_ot = (op_table_t *) so->so_tables->data;
	sqlo_natural_join_cond (so, left_ot, right_ot, tree);
