         */
        if(tvb_get_letohl(tvb,offset+8)
           || tvb_get_letohl(tvb,offset+12)
           || tvb_get_letohl(tvb,offset+20)
           || tvb_get_letohl(tvb,offset+40)
           || tvb_get_letohl(tvb,offset+44)){
            return 0;
        }
