        }
        /* byte 1 must be 0x80 */
        if(tvb_get_guint8(tvb, offset+1)!=0x80){
            return 0;
        }
        /* bytes 2,3 must be null */
        if(tvb_get_letohs(tvb,offset+2)){
            return 0;
        }
        /* ahs and dsl must be null */
        if(tvb_get_letohl(tvb,offset+4)){
