        tmpbyte=tvb_get_guint8(tvb,offset+1);
        if(!(tmpbyte&0x80)){
            return 0;
        }
        /* the reserved bits in byte 1 must be 0 */
        if(tmpbyte&0x61){
            return 0;
        }
        /* status must be known */
        if(!try_val_to_str(tvb_get_guint8(tvb,offset+3), scsi_status_val)){
