func_before:
static unsigned long
get_num_dynamic_syms (Filedata * filedata)
{
  unsigned long num_of_syms = 0;

  if (!do_histogram && (!do_using_dynamic || do_dyn_syms))
    return num_of_syms;

  if (dynamic_info[DT_HASH])
    {
      unsigned char nb[8];
      unsigned char nc[8];
      unsigned int hash_ent_size = 4;

      if ((filedata->file_header.e_machine == EM_ALPHA
	   || filedata->file_header.e_machine == EM_S390
	   || filedata->file_header.e_machine == EM_S390_OLD)
	  && filedata->file_header.e_ident[EI_CLASS] == ELFCLASS64)
	hash_ent_size = 8;

      if (fseek (filedata->handle,
		 (archive_file_offset
		  + offset_from_vma (filedata, dynamic_info[DT_HASH],
				     sizeof nb + sizeof nc)),
		 SEEK_SET))
	{
	  error (_("Unable to seek to start of dynamic information\n"));
	  goto no_hash;
	}

      if (fread (nb, hash_ent_size, 1, filedata->handle) != 1)
	{
	  error (_("Failed to read in number of buckets\n"));
	  goto no_hash;
	}

      if (fread (nc, hash_ent_size, 1, filedata->handle) != 1)
	{
	  error (_("Failed to read in number of chains\n"));
	  goto no_hash;
	}

      nbuckets = byte_get (nb, hash_ent_size);
      nchains = byte_get (nc, hash_ent_size);
      num_of_syms = nchains;

      buckets = get_dynamic_data (filedata, nbuckets, hash_ent_size);
      chains  = get_dynamic_data (filedata, nchains, hash_ent_size);

  no_hash:
      if (num_of_syms == 0)
	{
	  if (buckets)
	    {
	      free (buckets);
	      buckets = NULL;
	    }
	  if (chains)
	    {
	      free (chains);
	      buckets = NULL;
	    }
	  nbuckets = 0;
	}
    }

  if (dynamic_info_DT_GNU_HASH)
    {
      unsigned char nb[16];
      bfd_vma i, maxchain = 0xffffffff, bitmaskwords;
      bfd_vma buckets_vma;
      unsigned long hn;
      bfd_boolean gnu_hash_error = FALSE;

      if (fseek (filedata->handle,
		 (archive_file_offset
		  + offset_from_vma (filedata, dynamic_info_DT_GNU_HASH,
				     sizeof nb)),
		 SEEK_SET))
	{
	  error (_("Unable to seek to start of dynamic information\n"));
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      if (fread (nb, 16, 1, filedata->handle) != 1)
	{
	  error (_("Failed to read in number of buckets\n"));
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      ngnubuckets = byte_get (nb, 4);
      gnusymidx = byte_get (nb + 4, 4);
      bitmaskwords = byte_get (nb + 8, 4);
      buckets_vma = dynamic_info_DT_GNU_HASH + 16;
      if (is_32bit_elf)
	buckets_vma += bitmaskwords * 4;
      else
	buckets_vma += bitmaskwords * 8;

      if (fseek (filedata->handle,
		 (archive_file_offset
		  + offset_from_vma (filedata, buckets_vma, 4)),
		 SEEK_SET))
	{
	  error (_("Unable to seek to start of dynamic information\n"));
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      gnubuckets = get_dynamic_data (filedata, ngnubuckets, 4);

      if (gnubuckets == NULL)
	{
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      for (i = 0; i < ngnubuckets; i++)
	if (gnubuckets[i] != 0)
	  {
	    if (gnubuckets[i] < gnusymidx)
	      {
		gnu_hash_error = TRUE;
		return FALSE;
	      }

	    if (maxchain == 0xffffffff || gnubuckets[i] > maxchain)
	      maxchain = gnubuckets[i];
	  }

      if (maxchain == 0xffffffff)
	{
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      maxchain -= gnusymidx;

      if (fseek (filedata->handle,
		 (archive_file_offset
		  + offset_from_vma (filedata, buckets_vma
					   + 4 * (ngnubuckets + maxchain), 4)),
		 SEEK_SET))
	{
	  error (_("Unable to seek to start of dynamic information\n"));
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      do
	{
	  if (fread (nb, 4, 1, filedata->handle) != 1)
	    {
	      error (_("Failed to determine last chain length\n"));
	  gnu_hash_error = TRUE;
	      goto no_gnu_hash;
	    }

	  if (maxchain + 1 == 0)
	    {
	      gnu_hash_error = TRUE;
	      goto no_gnu_hash;
	    }

	  ++maxchain;
	}
      while ((byte_get (nb, 4) & 1) == 0);

      if (fseek (filedata->handle,
		 (archive_file_offset
		  + offset_from_vma (filedata, buckets_vma + 4 * ngnubuckets, 4)),
		 SEEK_SET))
	{
	  error (_("Unable to seek to start of dynamic information\n"));
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      gnuchains = get_dynamic_data (filedata, maxchain, 4);
      ngnuchains = maxchain;

      if (gnuchains == NULL)
	{
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      if (dynamic_info_DT_MIPS_XHASH)
	{
	  if (fseek (filedata->handle,
		     (archive_file_offset
		      + offset_from_vma (filedata, (buckets_vma
						    + 4 * (ngnubuckets
							   + maxchain)), 4)),
		     SEEK_SET))
	    {
	      error (_("Unable to seek to start of dynamic information\n"));
	      gnu_hash_error = TRUE;
	      goto no_gnu_hash;
	    }

	  mipsxlat = get_dynamic_data (filedata, maxchain, 4);
	}

      for (hn = 0; hn < ngnubuckets; ++hn)
	if (gnubuckets[hn] != 0)
	  {
	    bfd_vma si = gnubuckets[hn];
	    bfd_vma off = si - gnusymidx;

	    do
	      {
		if (dynamic_info_DT_MIPS_XHASH)
		  {
		    if (mipsxlat[off] >= num_of_syms)
		      num_of_syms = mipsxlat[off] + 1;
		  }
		else
		  {
		    if (si >= num_of_syms)
		      num_of_syms = si + 1;
		  }
		si++;
	      }
	    while (off < ngnuchains && (gnuchains[off++] & 1) == 0);
	  }

  no_gnu_hash:
      if (gnu_hash_error)
	{
	  if (mipsxlat)
	    {
	      free (mipsxlat);
	      mipsxlat = NULL;
	    }
	  if (gnuchains)
	    {
	      free (gnuchains);
	      gnuchains = NULL;
	    }
	  if (gnubuckets)
	    {
	      free (gnubuckets);
	      gnubuckets = NULL;
	    }
	  ngnubuckets = 0;
	  ngnuchains = 0;
	}
    }

  return num_of_syms;
}

