	{
	  if (fread (nb, 4, 1, filedata->handle) != 1)
	    {
	      error (_("Failed to determine last chain length\n"));
	  gnu_hash_error = TRUE;
	      goto no_gnu_hash;
	    }

	  if (maxchain + 1 == 0)
	    {
	      gnu_hash_error = TRUE;
	      goto no_gnu_hash;
	    }

	  ++maxchain;
	}
      while ((byte_get (nb, 4) & 1) == 0);

      if (fseek (filedata->handle,
		 (archive_file_offset
		  + offset_from_vma (filedata, buckets_vma + 4 * ngnubuckets, 4)),
