    VPackBuilder resultBuilder;
    {
      VPackObjectBuilder b(&resultBuilder);
      resultBuilder.add("jwt", VPackValue(generateJwt(username)));
    }

    isValid = true;
    generateDocument(resultBuilder.slice(), true, &VPackOptions::Defaults);
