	lookupOptions |= J9_LOOK_STATIC;
	if ((resolveFlags & J9_RESOLVE_FLAG_JCL_CONSTANT_POOL) == J9_RESOLVE_FLAG_JCL_CONSTANT_POOL) {
		cpClass = NULL;
