		goto done;
	}

	methodClass = J9_CLASS_FROM_METHOD(method);
	if (methodClass != resolvedClass) {
		if (isResolvedClassAnInterface) {
incompat:
			if (throwException) {
				setCurrentException(vmStruct, J9VMCONSTANTPOOL_JAVALANGINCOMPATIBLECLASSCHANGEERROR, NULL);
