				if (threadEventsPending(vmStruct)) {
					method = NULL;
					goto done;
				}
				if (preCount != vmStruct->javaVM->hotSwapCount) {
					goto tryAgain;
				}
			} else {
				/* Can't initialize the class, so fail the resolve */
				method = NULL;
				goto done;
			}
