         * must all be 0
         */
        if(tvb_get_letohl(tvb,offset+24)
           || tvb_get_letohl(tvb,offset+32)
           || tvb_get_letohl(tvb,offset+44)){
            return 0;
        }
        break;
    case ISCSI_OPCODE_SCSI_DATA_IN:
        /* top two bits in byte 0 must be 0 */
        if(tvb_get_guint8(tvb, offset+0)&0xc0){
            return 0;
