        guint32 pduLen = 48;
        guint8 secondPduByte = tvb_get_guint8(tvb, offset + 1);
        int badPdu = FALSE;
        guint8 ahsLen=0;
        guint32 data_segment_offset, data_segment_len_padded;

        /* mask out any extra bits in the opcode byte */
        opcode = tvb_get_guint8(tvb, offset + 0);
        opcode &= OPCODE_MASK;

