    if (available_bytes < 48) {
        /* heuristic already rejected the packet if size < 48,
           assume it's an iscsi packet with a segmented header */
        pinfo->desegment_offset = offset;
        pinfo->desegment_len = DESEGMENT_ONE_MORE_SEGMENT;
        return -1;
    }

