	  error (_("Unable to seek to start of dynamic information\n"));
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      gnuchains = get_dynamic_data (filedata, maxchain, 4);
      ngnuchains = maxchain;

      if (gnuchains == NULL)
	{
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      if (dynamic_info_DT_MIPS_XHASH)
	{
	  if (fseek (filedata->handle,
		     (archive_file_offset
		      + offset_from_vma (filedata, (buckets_vma
						    + 4 * (ngnubuckets
							   + maxchain)), 4)),
		     SEEK_SET))
	    {
	      error (_("Unable to seek to start of dynamic information\n"));
	      gnu_hash_error = TRUE;
