	TIFFDirEntry* dir;
	uint16_t dircount;
	TIFFDirEntry* dp;
	uint16_t di;
	const TIFFField* fip;
	uint32_t fii;
        (*tif->tif_cleanup)(tif);   /* cleanup any previous compression state */
	_TIFFSetupFields(tif, infoarray);
	dircount=TIFFFetchDirectory(tif,diroff,&dir,NULL);
	if (!dircount)
