    /* Set up structures needed to add the protocol subtree and manage it */
    guint offset = 0;
    guint32 available_bytes = tvb_captured_length(tvb);
    int digestsActive = 1;
    conversation_t *conversation = NULL;
    iscsi_session_t *iscsi_session=NULL;
