		    if (mipsxlat[off] >= num_of_syms)
		      num_of_syms = mipsxlat[off] + 1;
		  }
		else
		  {
		    if (si >= num_of_syms)
		      num_of_syms = si + 1;
		  }
		si++;
	      }
	    while (off < ngnuchains && (gnuchains[off++] & 1) == 0);
	  }

  no_gnu_hash:
      if (gnu_hash_error)
	{
	  if (mipsxlat)
	    {
	      free (mipsxlat);
	      mipsxlat = NULL;
	    }
	  if (gnuchains)
	    {
	      free (gnuchains);
	      gnuchains = NULL;
	    }
	  if (gnubuckets)
	    {
	      free (gnubuckets);
	      gnubuckets = NULL;
	    }
	  ngnubuckets = 0;
	  ngnuchains = 0;
	}
    }

  return num_of_syms;
}

