	      goto no_gnu_hash;
	    }

	  if (maxchain + 1 == 0)
	    {
	      gnu_hash_error = TRUE;
	      goto no_gnu_hash;
	    }

	  ++maxchain;
	}
      while ((byte_get (nb, 4) & 1) == 0);

      if (fseek (filedata->handle,
		 (archive_file_offset
		  + offset_from_vma (filedata, buckets_vma + 4 * ngnubuckets, 4)),
		 SEEK_SET))
	{
	  error (_("Unable to seek to start of dynamic information\n"));
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      gnuchains = get_dynamic_data (filedata, maxchain, 4);
      ngnuchains = maxchain;

      if (gnuchains == NULL)
	{
	  gnu_hash_error = TRUE;
	  goto no_gnu_hash;
	}

      if (dynamic_info_DT_MIPS_XHASH)
	{
	  if (fseek (filedata->handle,
		     (archive_file_offset
		      + offset_from_vma (filedata, (buckets_vma
						    + 4 * (ngnubuckets
							   + maxchain)), 4)),
		     SEEK_SET))
	    {
	      error (_("Unable to seek to start of dynamic information\n"));
	      gnu_hash_error = TRUE;
	      goto no_gnu_hash;
	    }

	  mipsxlat = get_dynamic_data (filedata, maxchain, 4);
	}

      for (hn = 0; hn < ngnubuckets; ++hn)
	if (gnubuckets[hn] != 0)
	  {
	    bfd_vma si = gnubuckets[hn];
	    bfd_vma off = si - gnusymidx;

	    do
	      {
		if (dynamic_info_DT_MIPS_XHASH)
		  {
		    if (mipsxlat[off] >= num_of_syms)
		      num_of_syms = mipsxlat[off] + 1;
		  }
		else
		  {
		    if (si >= num_of_syms)
		      num_of_syms = si + 1;
		  }
		si++;
	      }
	    while (off < ngnuchains && (gnuchains[off++] & 1) == 0);
	  }

  no_gnu_hash:
      if (gnu_hash_error)
	{
	  if (mipsxlat)
	    {
	      free (mipsxlat);
	      mipsxlat = NULL;
	    }
	  if (gnuchains)
	    {
	      free (gnuchains);
	      gnuchains = NULL;
	    }
	  if (gnubuckets)
	    {
	      free (gnubuckets);
	      gnubuckets = NULL;
	    }
