        case 0x80:
        case 0x40:
        case 0x00:
            break;
        default:
            return 0;
        }
        /* CSG and NSG must not be 2 */
        if(((tmpbyte & 0x03) == 0x02)
           || ((tmpbyte & 0x0c) == 0x08)) {
            return 0;
        }
        /* if T bit is set NSG must not be 0 */
        if(tmpbyte&0x80){
            if(!(tmpbyte&0x03)){
                return 0;
            }
        }
        /* should we test that datasegmentlen is non zero? */
        break;
    case ISCSI_OPCODE_LOGIN_RESPONSE:
