         * and the two reserved bits in byte 1 must be 0
         */
        tmpbyte=tvb_get_guint8(tvb, offset+1);
        switch(tmpbyte&0xf0){
        case 0x80:
        case 0x40:
        case 0x00:
            break;
        default:
            return 0;
        }
